import { useState, useEffect, useRef } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface ChatMessage {
  id: string;
  message: string;
  response?: string;
  isBot: boolean;
  createdAt: string;
}

export default function ChatWidget() {
  const { toast } = useToast();
  const [isOpen, setIsOpen] = useState(false);
  const [newMessage, setNewMessage] = useState("");
  const scrollAreaRef = useRef<HTMLDivElement>(null);

  const { data: chatHistory, refetch: refetchHistory } = useQuery({
    queryKey: ["/api/chat/history", { limit: 20 }],
    enabled: isOpen,
    retry: false,
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/chat", { message });
      return response.json();
    },
    onSuccess: () => {
      setNewMessage("");
      refetchHistory();
      // Scroll to bottom after message is sent
      setTimeout(() => {
        if (scrollAreaRef.current) {
          scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
        }
      }, 100);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to send message",
        variant: "destructive",
      });
    },
  });

  // Scroll to bottom when chat history updates
  useEffect(() => {
    if (chatHistory && scrollAreaRef.current) {
      setTimeout(() => {
        if (scrollAreaRef.current) {
          scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
        }
      }, 100);
    }
  }, [chatHistory]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newMessage.trim() || sendMessageMutation.isPending) return;

    sendMessageMutation.mutate(newMessage.trim());
  };

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  // Group messages by conversation pairs
  const groupedMessages: Array<{ userMessage: ChatMessage; botResponse?: ChatMessage }> = [];
  if (chatHistory && Array.isArray(chatHistory)) {
    const messages = [...(chatHistory || [])].reverse(); // Most recent first, then reverse for display

    for (let i = 0; i < messages.length; i++) {
      const message = messages[i];
      if (!message.isBot) {
        // Find the corresponding bot response
        const botResponse = messages.find((m, idx) => 
          idx > i && m.isBot && new Date(m.createdAt).getTime() > new Date(message.createdAt).getTime()
        );

        groupedMessages.push({
          userMessage: message,
          botResponse,
        });
      }
    }
  }

  return (
    <div className="fixed bottom-6 right-6 z-50">
      {/* Chat Interface */}
      {isOpen && (
        <Card className="absolute bottom-16 right-0 w-80 h-96 shadow-xl border border-border">
          <CardHeader className="bg-primary text-primary-foreground p-4 rounded-t-xl">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center mr-2">
                  <i className="fas fa-robot text-sm"></i>
                </div>
                <div>
                  <h4 className="font-medium">Career Mirror Assistant</h4>
                  <p className="text-xs opacity-90">Online now</p>
                </div>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={toggleChat}
                className="text-white/80 hover:text-white hover:bg-white/10 p-1 h-auto"
                data-testid="button-close-chat"
              >
                <i className="fas fa-times"></i>
              </Button>
            </div>
          </CardHeader>

          <ScrollArea className="flex-1 h-64 p-4" ref={scrollAreaRef}>
            <div className="space-y-4">
              {/* Initial bot message */}
              <div className="flex items-start">
                <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center mr-2 flex-shrink-0">
                  <i className="fas fa-robot text-white text-xs"></i>
                </div>
                <div className="bg-secondary p-3 rounded-lg max-w-xs">
                  <p className="text-sm">
                    Hi! I'm here to help you find the perfect classes and career guidance. What are you looking for today?
                  </p>
                </div>
              </div>

              {/* Chat history */}
              {groupedMessages.map((conversation, index) => (
                <div key={conversation.userMessage.id} className="space-y-2">
                  {/* User message */}
                  <div className="flex justify-end">
                    <div className="bg-primary text-primary-foreground p-3 rounded-lg max-w-xs">
                      <p className="text-sm" data-testid={`user-message-${index}`}>
                        {conversation.userMessage.message}
                      </p>
                    </div>
                  </div>

                  {/* Bot response */}
                  {conversation.botResponse && (
                    <div className="flex items-start">
                      <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center mr-2 flex-shrink-0">
                        <i className="fas fa-robot text-white text-xs"></i>
                      </div>
                      <div className="bg-secondary p-3 rounded-lg max-w-xs">
                        <p className="text-sm" data-testid={`bot-response-${index}`}>
                          {conversation.botResponse.message}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              ))}

              {/* Loading indicator */}
              {sendMessageMutation.isPending && (
                <div className="flex items-start">
                  <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center mr-2 flex-shrink-0">
                    <i className="fas fa-robot text-white text-xs"></i>
                  </div>
                  <div className="bg-secondary p-3 rounded-lg">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: "0.1s" }}></div>
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: "0.2s" }}></div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </ScrollArea>

          <CardContent className="p-4 border-t border-border">
            <form onSubmit={handleSendMessage} className="flex gap-2">
              <Input
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1"
                disabled={sendMessageMutation.isPending}
                data-testid="input-chat-message"
              />
              <Button
                type="submit"
                disabled={sendMessageMutation.isPending || !newMessage.trim()}
                className="bg-primary text-primary-foreground hover:bg-primary/90"
                data-testid="button-send-message"
              >
                <i className="fas fa-paper-plane text-sm"></i>
              </Button>
            </form>
          </CardContent>
        </Card>
      )}

      {/* Chat Toggle Button */}
      <Button
        onClick={toggleChat}
        className="w-14 h-14 bg-primary text-primary-foreground rounded-full shadow-lg hover:bg-primary/90 transition-all hover:scale-105 flex items-center justify-center"
        data-testid="button-toggle-chat"
      >
        <i className={`fas ${isOpen ? "fa-times" : "fa-comments"} text-lg`}></i>
      </Button>
    </div>
  );
}

client/src/components/ClassCard.tsx.content:
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface ClassCardProps {
  classData: {
    id: string;
    title: string;
    description: string;
    category: string;
    instructor?: string;
    price: string;
    duration: string;
    location: string;
    isOnline: boolean;
    startDate: string;
    maxStudents: number;
    currentStudents: number;
    skills: string[];
    difficulty: string;
    spotsLeft: number;
    relevanceScore?: number;
  };
}

export default function ClassCard({ classData }: ClassCardProps) {
  const { toast } = useToast();

  const enrollMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", `/api/classes/${classData.id}/enroll`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Successfully enrolled in class",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/classes"] });
      queryClient.invalidateQueries({ queryKey: ["/api/enrollments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to enroll in class",
        variant: "destructive",
      });
    },
  });

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      weekday: "short",
      month: "short",
      day: "numeric",
    });
  };

  const getCategoryColor = (category: string) => {
    switch (category.toLowerCase()) {
      case "programming":
        return "bg-accent/10 text-accent";
      case "design":
        return "bg-blue-100 text-blue-800";
      case "business":
        return "bg-green-100 text-green-800";
      case "marketing":
        return "bg-purple-100 text-purple-800";
      case "data science":
        return "bg-indigo-100 text-indigo-800";
      default:
        return "bg-secondary text-secondary-foreground";
    }
  };

  const getSpotsColor = (spotsLeft: number) => {
    if (spotsLeft <= 2) return "text-red-600";
    if (spotsLeft <= 5) return "text-orange-600";
    return "text-green-600";
  };

  return (
    <Card className="border-border hover:shadow-lg transition-shadow">
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center mb-2">
              <Badge className={`${getCategoryColor(classData.category)} mr-2`}>
                {classData.category}
              </Badge>
              <span className={`text-sm font-medium ${getSpotsColor(classData.spotsLeft)}`}>
                {classData.spotsLeft > 0 ? `${classData.spotsLeft} spots left` : "Full"}
              </span>
            </div>

            <h3 className="text-lg font-semibold text-foreground mb-2" data-testid="text-class-title">
              {classData.title}
            </h3>

            <p className="text-muted-foreground text-sm mb-3 line-clamp-2" data-testid="text-class-description">
              {classData.description}
            </p>

            <div className="flex items-center text-sm text-muted-foreground space-x-4 mb-3">
              <div className="flex items-center">
                <i className="fas fa-calendar mr-1"></i>
                <span data-testid="text-class-date">
                  {classData.startDate ? `Starts ${formatDate(classData.startDate)}` : "Date TBD"}
                </span>
              </div>
              <div className="flex items-center">
                <i className="fas fa-clock mr-1"></i>
                <span data-testid="text-class-duration">{classData.duration}</span>
              </div>
              <div className="flex items-center">
                <i className={`${classData.isOnline ? "fas fa-globe" : "fas fa-map-marker-alt"} mr-1`}></i>
                <span data-testid="text-class-location">
                  {classData.isOnline ? "Online" : classData.location}
                </span>
              </div>
            </div>

            {classData.instructor && (
              <div className="flex items-center text-sm text-muted-foreground mb-3">
                <i className="fas fa-user mr-1"></i>
                <span data-testid="text-class-instructor">{classData.instructor}</span>
              </div>
            )}

            {classData.skills && classData.skills.length > 0 && (
              <div className="flex flex-wrap gap-1 mb-3">
                {classData.skills.slice(0, 4).map((skill, index) => (
                  <Badge key={index} variant="outline" className="text-xs">
                    {skill}
                  </Badge>
                ))}
                {classData.skills.length > 4 && (
                  <Badge variant="outline" className="text-xs">
                    +{classData.skills.length - 4} more
                  </Badge>
                )}
              </div>
            )}

            {classData.difficulty && (
              <Badge variant="secondary" className="text-xs">
                {classData.difficulty.charAt(0).toUpperCase() + classData.difficulty.slice(1)}
              </Badge>
            )}
          </div>

          <div className="text-right ml-4">
            <div className="text-2xl font-bold text-foreground mb-2" data-testid="text-class-price">
              ${classData.price}
            </div>
            <Button
              onClick={() => enrollMutation.mutate()}
              disabled={enrollMutation.isPending || classData.spotsLeft <= 0}
              className="bg-primary text-primary-foreground hover:bg-primary/90"
              data-testid="button-enroll"
            >
              {enrollMutation.isPending ? "Enrolling..." : classData.spotsLeft > 0 ? "Enroll Now" : "Full"}
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/DoppelgangerCard.t.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface DoppelgangerCardProps {
  doppelganger: {
    userId: string;
    profileData: {
      name?: string;
      currentRole?: string;
      experience?: number;
      skills?: Array<{ skillName: string; proficiency: number }>;
      careerPaths?: Array<{
        role: string;
        company?: string;
        startDate?: string;
        endDate?: string;
        isCurrent?: boolean;
      }>;
    };
    similarity: number;
    careerPath?: string;
    currentRole?: string;
    skills?: string[];
  };
}

export default function DoppelgangerCard({ doppelganger }: DoppelgangerCardProps) {
  const getInitials = (name?: string) => {
    if (!name) return "??";
    const parts = name.split(" ");
    return parts.length >= 2 
      ? `${parts[0][0]}${parts[parts.length - 1][0]}`.toUpperCase()
      : name.slice(0, 2).toUpperCase();
  };

  const getGradientClass = (similarity: number) => {
    if (similarity >= 0.8) return "from-primary to-accent";
    if (similarity >= 0.6) return "from-accent to-primary";
    return "from-secondary to-muted";
  };

  const generateCareerSummary = () => {
    if (doppelganger.careerPath) {
      return doppelganger.careerPath;
    }

    const paths = doppelganger.profileData?.careerPaths;
    if (!paths || paths.length === 0) {
      return "Career path information not available";
    }

    const sortedPaths = paths.sort((a, b) => 
      new Date(a.startDate || "1970-01-01").getTime() - new Date(b.startDate || "1970-01-01").getTime()
    );

    const pathSummary = sortedPaths.map(path => path.role).join("  ");
    const timeframe = paths.length > 1 ? `(${paths.length} roles)` : "(1 role)";

    return `${pathSummary} ${timeframe}`;
  };

  const displayName = doppelganger.profileData?.name || "Anonymous Professional";
  const displayRole = doppelganger.currentRole || doppelganger.profileData?.currentRole || "Professional";
  const displaySkills = doppelganger.skills || doppelganger.profileData?.skills?.map(s => s.skillName) || [];
  const matchPercentage = Math.round(doppelganger.similarity * 100);

  return (
    <div className="flex items-center p-4 bg-secondary rounded-lg hover:bg-secondary/80 transition-colors">
      <div className={`w-12 h-12 bg-gradient-to-br ${getGradientClass(doppelganger.similarity)} rounded-full flex items-center justify-center text-white font-semibold`}>
        {getInitials(displayName)}
      </div>

      <div className="ml-4 flex-1">
        <h4 className="font-semibold text-foreground" data-testid="text-doppelganger-name">
          {displayName} - {displayRole}
        </h4>
        <p className="text-sm text-muted-foreground mb-2" data-testid="text-doppelganger-path">
          {generateCareerSummary()}
        </p>

        {displaySkills.length > 0 && (
          <div className="flex items-center gap-1 flex-wrap">
            {displaySkills.slice(0, 3).map((skill, index) => (
              <Badge key={index} variant="outline" className="text-xs">
                {skill}
              </Badge>
            ))}
            {displaySkills.length > 3 && (
              <Badge variant="outline" className="text-xs">
                +{displaySkills.length - 3} more
              </Badge>
            )}
          </div>
        )}
      </div>

      <div className="text-right">
        <div className="text-sm font-semibold text-foreground mb-1" data-testid="text-doppelganger-match">
          {matchPercentage}% match
        </div>
        <Button 
          variant="link" 
          size="sm" 
          className="text-primary hover:underline p-0 h-auto"
          data-testid="button-view-path"
        >
          View Path
        </Button>
      </div>
    </div>
  );
}

client/src/components/FileUpload.tsx.content:
import { useState, useRef } from "react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

export default function FileUpload() {
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [dragActive, setDragActive] = useState(false);

  const uploadMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append("resume", file);

      // Simulate upload progress
      setUploadProgress(10);

      const response = await fetch("/api/profile/upload-resume", {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      setUploadProgress(50);

      if (!response.ok) {
        const text = await response.text();
        throw new Error(`${response.status}: ${text}`);
      }

      setUploadProgress(90);
      const result = await response.json();
      setUploadProgress(100);

      return result;
    },
    onSuccess: (data) => {
      toast({
        title: "Success",
        description: "Resume uploaded and parsed successfully",
      });
      setUploadProgress(0);

      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ["/api/profile"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard"] });
    },
    onError: (error: Error) => {
      setUploadProgress(0);

      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Upload Failed",
        description: error.message || "Failed to upload resume",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (file: File) => {
    // Validate file type
    const allowedTypes = [
      'application/pdf',
      'application/msword',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'text/plain'
    ];

    if (!allowedTypes.includes(file.type)) {
      toast({
        title: "Invalid File Type",
        description: "Please upload a PDF, DOC, DOCX, or TXT file",
        variant: "destructive",
      });
      return;
    }

    // Validate file size (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File Too Large",
        description: "Please upload a file smaller than 5MB",
        variant: "destructive",
      });
      return;
    }

    uploadMutation.mutate(file);
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFileSelect(file);
    }
  };

  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const file = e.dataTransfer.files?.[0];
    if (file) {
      handleFileSelect(file);
    }
  };

  const openFileDialog = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="space-y-4">
      <Card
        className={`border-2 border-dashed transition-colors cursor-pointer ${
          dragActive
            ? "border-primary bg-primary/5"
            : "border-border hover:border-primary/50"
        }`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        onClick={openFileDialog}
        data-testid="file-upload-area"
      >
        <CardContent className="p-8 text-center">
          <div className="space-y-4">
            <div className={`w-16 h-16 mx-auto rounded-full flex items-center justify-center ${
              dragActive ? "bg-primary text-primary-foreground" : "bg-secondary text-secondary-foreground"
            }`}>
              <i className="fas fa-cloud-upload-alt text-2xl"></i>
            </div>

            <div>
              <h3 className="text-lg font-semibold text-foreground mb-2">
                {dragActive ? "Drop your file here" : "Upload your CV/Resume"}
              </h3>
              <p className="text-muted-foreground text-sm mb-4">
                Drag and drop your file here, or click to browse
              </p>
              <p className="text-xs text-muted-foreground">
                Supports PDF, DOC, DOCX, and TXT files up to 5MB
              </p>
            </div>

            <Button
              variant="outline"
              disabled={uploadMutation.isPending}
              className="mt-4"
              data-testid="button-browse-files"
            >
              <i className="fas fa-folder-open mr-2"></i>
              Browse Files
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Upload Progress */}
      {uploadMutation.isPending && (
        <Card className="border-border">
          <CardContent className="p-4">
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span className="text-foreground">Uploading and parsing resume...</span>
                <span className="text-muted-foreground">{uploadProgress}%</span>
              </div>
              <Progress value={uploadProgress} className="w-full" />
            </div>
          </CardContent>
        </Card>
      )}

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept=".pdf,.doc,.docx,.txt"
        onChange={handleFileInputChange}
        className="hidden"
        data-testid="file-input"
      />

      {/* Upload Instructions */}
      <div className="bg-muted/50 rounded-lg p-4">
        <h4 className="font-medium text-foreground mb-2">What happens after upload?</h4>
        <ul className="text-sm text-muted-foreground space-y-1">
          <li className="flex items-center">
            <i className="fas fa-check text-green-500 mr-2 text-xs"></i>
            AI will extract your skills, experience, and career history
          </li>
          <li className="flex items-center">
            <i className="fas fa-check text-green-500 mr-2 text-xs"></i>
            Your profile will be automatically updated
          </li>
          <li className="flex items-center">
            <i className="fas fa-check text-green-500 mr-2 text-xs"></i>
            You'll get personalized career recommendations
          </li>
          <li className="flex items-center">
            <i className="fas fa-check text-green-500 mr-2 text-xs"></i>
            Career doppelgnger matching will be enabled
          </li>
        </ul>
      </div>
    </div>
  );
}

client/src/components/ProfileCompletion..content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";

interface ProfileCompletionProps {
  user?: {
    firstName?: string;
    lastName?: string;
    email?: string;
  };
  profile?: {
    currentRole?: string;
    targetRole?: string;
    education?: string;
    resumeText?: string;
    profileCompletion?: number;
  };
  skills?: Array<{
    skillName: string;
    proficiency: number;
  }>;
}

export default function ProfileCompletion({ user, profile, skills }: ProfileCompletionProps) {
  const completionScore = profile?.profileCompletion || 0;

  const completionItems = [
    {
      label: "Basic information",
      completed: !!(user?.firstName && user?.email),
      icon: "fas fa-user",
    },
    {
      label: "Current role",
      completed: !!profile?.currentRole,
      icon: "fas fa-briefcase",
    },
    {
      label: "Target role",
      completed: !!profile?.targetRole,
      icon: "fas fa-target",
    },
    {
      label: "Skills & interests",
      completed: !!(skills && skills.length > 0),
      icon: "fas fa-cogs",
    },
    {
      label: "Upload CV/Resume",
      completed: !!profile?.resumeText,
      icon: "fas fa-file-alt",
    },
  ];

  const nextIncompleteItem = completionItems.find(item => !item.completed);

  return (
    <Card className="border-border">
      <CardHeader>
        <CardTitle className="text-lg">Complete Your Profile</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="text-sm text-muted-foreground">Profile completion</span>
            <span className="text-sm font-medium text-foreground" data-testid="text-completion-percentage">
              {completionScore}%
            </span>
          </div>

          <Progress value={completionScore} className="w-full h-2" />

          <div className="space-y-3 mt-4">
            {completionItems.map((item, index) => (
              <div key={index} className="flex items-center text-sm" data-testid={`completion-item-${index}`}>
                <i className={`${item.completed ? "fas fa-check-circle text-green-500" : "fas fa-circle text-muted-foreground/40"} mr-2`}></i>
                <span className={item.completed ? "text-foreground" : "text-muted-foreground"}>
                  {item.label}
                </span>
              </div>
            ))}
          </div>

          {nextIncompleteItem && (
            <div className="mt-4 pt-4 border-t border-border">
              <div className="mb-2">
                <span className="text-sm font-medium text-foreground">Next step:</span>
                <span className="text-sm text-muted-foreground ml-1">{nextIncompleteItem.label}</span>
              </div>

              {nextIncompleteItem.label === "Upload CV/Resume" ? (
                <Button 
                  className="w-full bg-accent text-accent-foreground hover:bg-accent/90"
                  onClick={() => window.location.href = "/profile"}
                  data-testid="button-upload-resume"
                >
                  <i className="fas fa-upload mr-2"></i>
                  Upload CV/Resume
                </Button>
              ) : (
                <Button 
                  variant="outline"
                  className="w-full"
                  onClick={() => window.location.href = "/profile"}
                  data-testid="button-complete-profile"
                >
                  <i className={`${nextIncompleteItem.icon} mr-2`}></i>
                  Complete Profile
                </Button>
              )}
            </div>
          )}

          {completionScore === 100 && (
            <div className="mt-4 pt-4 border-t border-border text-center">
              <div className="flex items-center justify-center text-green-600 mb-2">
                <i className="fas fa-trophy mr-2"></i>
                <span className="font-medium">Profile Complete!</span>
              </div>
              <p className="text-sm text-muted-foreground">
                Your profile is fully optimized for the best career recommendations.
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

