import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import Navigation from "@/components/Navigation";
import Sidebar from "@/components/Sidebar";
import { motion } from "framer-motion";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Achievement {
  id: string;
  name: string;
  description: string;
  category: string;
  icon: string;
  points: number;
  isUnlocked: boolean;
  unlockedAt?: string;
  progress?: number;
  maxProgress?: number;
  requirements: string[];
}

interface UserStats {
  totalPoints: number;
  level: number;
  pointsToNextLevel: number;
  totalAchievements: number;
  unlockedAchievements: number;
  streakDays: number;
  profileCompletionPercent: number;
}

interface Milestone {
  id: string;
  title: string;
  description: string;
  category: string;
  targetValue: number;
  currentValue: number;
  isCompleted: boolean;
  reward: string;
  deadline?: string;
}

export default function Achievements() {
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const { toast } = useToast();

  const { data: achievementsData, isLoading: achievementsLoading } = useQuery<{
    achievements: Achievement[];
    userStats: UserStats;
    milestones: Milestone[];
  }>({
    queryKey: ["/api/achievements"],
    retry: false,
  });

  const claimRewardMutation = useMutation({
    mutationFn: async (achievementId: string) => {
      return apiRequest(`/api/achievements/${achievementId}/claim`, "POST");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/achievements"] });
      toast({
        title: "Reward Claimed!",
        description: "Your achievement reward has been added to your account.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to claim reward. Please try again.",
        variant: "destructive",
      });
    },
  });

  const achievements = achievementsData?.achievements || [];
  const userStats = achievementsData?.userStats || {
    totalPoints: 0,
    level: 1,
    pointsToNextLevel: 100,
    totalAchievements: 0,
    unlockedAchievements: 0,
    streakDays: 0,
    profileCompletionPercent: 0,
  };
  const milestones = achievementsData?.milestones || [];

  const categories = [
    { id: "all", name: "All", icon: "fas fa-trophy" },
    { id: "profile", name: "Profile", icon: "fas fa-user" },
    { id: "learning", name: "Learning", icon: "fas fa-graduation-cap" },
    { id: "career", name: "Career", icon: "fas fa-briefcase" },
    { id: "social", name: "Social", icon: "fas fa-users" },
    { id: "milestone", name: "Milestones", icon: "fas fa-flag" },
  ];

  const filteredAchievements = selectedCategory === "all" 
    ? achievements 
    : achievements.filter(achievement => achievement.category === selectedCategory);

  const getXPForLevel = (level: number) => {
    return level * 100; // Simple progression: 100 XP per level
  };

  const getLevelProgress = () => {
    const currentLevelXP = getXPForLevel(userStats.level - 1);
    const nextLevelXP = getXPForLevel(userStats.level);
    const progressXP = userStats.totalPoints - currentLevelXP;
    const neededXP = nextLevelXP - currentLevelXP;
    return (progressXP / neededXP) * 100;
  };

  if (achievementsLoading) {
    return (
      <div className="min-h-screen bg-background">
        <Navigation />
        <div className="flex">
          <Sidebar />
          <main className="lg:pl-64 flex-1">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
              <div className="flex items-center justify-center h-64">
                <div className="text-center">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
                  <p className="text-muted-foreground">Loading achievements...</p>
                </div>
              </div>
            </div>
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <Navigation />
      <div className="flex">
        <Sidebar />
        <main className="lg:pl-64 flex-1">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="mb-8">
                <h1 className="text-3xl font-bold text-foreground mb-2">
                  Achievements & Progress
                </h1>
                <p className="text-muted-foreground">
                  Track your career development journey and unlock rewards
                </p>
              </div>

              {/* User Stats Dashboard */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                <Card>
                  <CardContent className="pt-6">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-primary mb-2" data-testid="user-level">
                        Level {userStats.level}
                      </div>
                      <p className="text-sm text-muted-foreground">Current Level</p>
                      <div className="mt-3">
                        <Progress value={getLevelProgress()} className="h-2" />
                        <p className="text-xs text-muted-foreground mt-1">
                          {userStats.pointsToNextLevel} XP to next level
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-green-600 mb-2" data-testid="total-points">
                        {userStats.totalPoints.toLocaleString()}
                      </div>
                      <p className="text-sm text-muted-foreground">Total XP</p>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-blue-600 mb-2" data-testid="achievements-unlocked">
                        {userStats.unlockedAchievements}/{userStats.totalAchievements}
                      </div>
                      <p className="text-sm text-muted-foreground">Achievements</p>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="pt-6">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-orange-600 mb-2" data-testid="streak-days">
                        {userStats.streakDays}
                      </div>
                      <p className="text-sm text-muted-foreground">Day Streak</p>
                    </div>
                  </CardContent>
                </Card>
              </div>

              <Tabs value={selectedCategory} onValueChange={setSelectedCategory} className="w-full">
                <TabsList className="grid w-full grid-cols-6">
                  {categories.map((category) => (
                    <TabsTrigger 
                      key={category.id} 
                      value={category.id}
                      className="text-xs"
                      data-testid={`tab-${category.id}`}
                    >
                      <i className={`${category.icon} mr-1`}></i>
                      {category.name}
                    </TabsTrigger>
                  ))}
                </TabsList>

                <TabsContent value={selectedCategory} className="mt-6">
                  {selectedCategory === "milestone" ? (
                    /* Milestones View */
                    <div className="space-y-4">
                      <h2 className="text-xl font-semibold mb-4">Current Milestones</h2>
                      {milestones.length > 0 ? (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {milestones.map((milestone, index) => (
                            <motion.div
                              key={milestone.id}
                              initial={{ opacity: 0, y: 20 }}
                              animate={{ opacity: 1, y: 0 }}
                              transition={{ duration: 0.3, delay: index * 0.1 }}
                            >
                              <Card className={`${milestone.isCompleted ? 'bg-green-50 dark:bg-green-900/20' : ''}`}>
                                <CardHeader className="pb-3">
                                  <div className="flex items-center justify-between">
                                    <CardTitle className="text-lg">{milestone.title}</CardTitle>
                                    {milestone.isCompleted && (
                                      <Badge variant="default" className="bg-green-600">
                                        <i className="fas fa-check mr-1"></i>
                                        Complete
                                      </Badge>
                                    )}
                                  </div>
                                </CardHeader>
                                <CardContent>
                                  <p className="text-sm text-muted-foreground mb-4">
                                    {milestone.description}
                                  </p>

                                  <div className="mb-4">
                                    <div className="flex justify-between text-sm mb-2">
                                      <span>Progress</span>
                                      <span className="font-medium">
                                        {milestone.currentValue}/{milestone.targetValue}
                                      </span>
                                    </div>
                                    <Progress 
                                      value={(milestone.currentValue / milestone.targetValue) * 100} 
                                      className="h-2"
                                    />
                                  </div>

                                  <div className="flex items-center justify-between text-sm">
                                    <span className="text-muted-foreground">Reward:</span>
                                    <Badge variant="outline">{milestone.reward}</Badge>
                                  </div>

                                  {milestone.deadline && (
                                    <div className="flex items-center justify-between text-sm mt-2">
                                      <span className="text-muted-foreground">Deadline:</span>
                                      <span className="text-xs">
                                        {new Date(milestone.deadline).toLocaleDateString()}
                                      </span>
                                    </div>
                                  )}
                                </CardContent>
                              </Card>
                            </motion.div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-center py-12">
                          <i className="fas fa-flag text-4xl text-muted-foreground mb-4"></i>
                          <h3 className="text-lg font-semibold text-foreground mb-2">No Active Milestones</h3>
                          <p className="text-muted-foreground">
                            Complete more activities to unlock career milestones
                          </p>
                        </div>
                      )}
                    </div>
                  ) : (
                    /* Achievements View */
                    <div className="space-y-4">
                      {filteredAchievements.length > 0 ? (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {filteredAchievements.map((achievement, index) => (
                            <motion.div
                              key={achievement.id}
                              initial={{ opacity: 0, y: 20 }}
                              animate={{ opacity: 1, y: 0 }}
                              transition={{ duration: 0.3, delay: index * 0.1 }}
                            >
                              <Card 
                                className={`relative transition-all duration-300 hover:shadow-lg ${
                                  achievement.isUnlocked 
                                    ? 'bg-gradient-to-br from-yellow-50 to-orange-50 dark:from-yellow-900/20 dark:to-orange-900/20' 
                                    : 'opacity-75'
                                }`}
                                data-testid={`achievement-${achievement.id}`}
                              >
                                {achievement.isUnlocked && (
                                  <div className="absolute top-2 right-2">
                                    <i className="fas fa-crown text-yellow-500 text-lg"></i>
                                  </div>
                                )}

                                <CardHeader className="pb-3">
                                  <div className="flex items-center space-x-3">
                                    <div className={`w-12 h-12 rounded-full flex items-center justify-center text-xl ${
                                      achievement.isUnlocked 
                                        ? 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-400' 
                                        : 'bg-gray-100 text-gray-400 dark:bg-gray-800 dark:text-gray-600'
                                    }`}>
                                      <i className={achievement.icon}></i>
                                    </div>
                                    <div className="flex-1">
                                      <CardTitle className={`text-lg ${achievement.isUnlocked ? 'text-foreground' : 'text-muted-foreground'}`}>
                                        {achievement.name}
                                      </CardTitle>
                                      <Badge 
                                        variant={achievement.isUnlocked ? "default" : "secondary"}
                                        className="mt-1"
                                      >
                                        {achievement.points} XP
                                      </Badge>
                                    </div>
                                  </div>
                                </CardHeader>

                                <CardContent>
                                  <p className={`text-sm mb-4 ${achievement.isUnlocked ? 'text-foreground' : 'text-muted-foreground'}`}>
                                    {achievement.description}
                                  </p>

                                  {achievement.progress !== undefined && achievement.maxProgress && (
                                    <div className="mb-4">
                                      <div className="flex justify-between text-sm mb-2">
                                        <span>Progress</span>
                                        <span className="font-medium">
                                          {achievement.progress}/{achievement.maxProgress}
                                        </span>
                                      </div>
                                      <Progress 
                                        value={(achievement.progress / achievement.maxProgress) * 100} 
                                        className="h-2"
                                      />
                                    </div>
                                  )}

                                  <div className="space-y-2">
                                    <p className="text-xs font-medium text-muted-foreground">Requirements:</p>
                                    <ul className="text-xs space-y-1">
                                      {achievement.requirements.map((req, reqIndex) => (
                                        <li key={reqIndex} className="flex items-center space-x-2">
                                          <i className={`fas fa-${achievement.isUnlocked ? 'check text-green-500' : 'circle text-gray-400'} text-xs`}></i>
                                          <span className={achievement.isUnlocked ? 'text-foreground' : 'text-muted-foreground'}>
                                            {req}
                                          </span>
                                        </li>
                                      ))}
                                    </ul>
                                  </div>

                                  {achievement.isUnlocked && achievement.unlockedAt && (
                                    <div className="mt-4 pt-4 border-t border-border">
                                      <div className="flex items-center justify-between">
                                        <span className="text-xs text-muted-foreground">
                                          Unlocked: {new Date(achievement.unlockedAt).toLocaleDateString()}
                                        </span>
                                        <Button
                                          size="sm"
                                          variant="outline"
                                          onClick={() => claimRewardMutation.mutate(achievement.id)}
                                          disabled={claimRewardMutation.isPending}
                                          data-testid={`claim-${achievement.id}`}
                                        >
                                          {claimRewardMutation.isPending ? "Claiming..." : "Claim Reward"}
                                        </Button>
                                      </div>
                                    </div>
                                  )}
                                </CardContent>
                              </Card>
                            </motion.div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-center py-12">
                          <i className="fas fa-trophy text-4xl text-muted-foreground mb-4"></i>
                          <h3 className="text-lg font-semibold text-foreground mb-2">No Achievements Found</h3>
                          <p className="text-muted-foreground">
                            {selectedCategory === "all" 
                              ? "Start your career journey to unlock achievements!" 
                              : `No ${selectedCategory} achievements available yet.`}
                          </p>
                        </div>
                      )}
                    </div>
                  )}
                </TabsContent>
              </Tabs>
            </motion.div>
          </div>
        </main>
      </div>
    </div>
  );
}

server/routes/achievements.ts.content:
import type { Express } from "express";
import { isAuthenticated } from "../replitAuth";
import { storage } from "../storage";

interface Achievement {
  id: string;
  name: string;
  description: string;
  category: string;
  icon: string;
  points: number;
  isUnlocked: boolean;
  unlockedAt?: string;
  progress?: number;
  maxProgress?: number;
  requirements: string[];
}

interface UserStats {
  totalPoints: number;
  level: number;
  pointsToNextLevel: number;
  totalAchievements: number;
  unlockedAchievements: number;
  streakDays: number;
  profileCompletionPercent: number;
}

interface Milestone {
  id: string;
  title: string;
  description: string;
  category: string;
  targetValue: number;
  currentValue: number;
  isCompleted: boolean;
  reward: string;
  deadline?: string;
}

export function registerAchievementRoutes(app: Express) {
  // Get user achievements, stats, and milestones
  app.get('/api/achievements', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get all achievements from database
      const allAchievements = await storage.getAchievements();

      // Get user's earned achievements
      const userAchievements = await storage.getUserAchievements(userId);

      // Map achievements with user progress
      const achievementsWithProgress = allAchievements.map(achievement => {
        const userAchievement = userAchievements.find(ua => ua.achievementId === achievement.id);
        return {
          id: achievement.id,
          name: achievement.name,
          description: achievement.description || "",
          category: achievement.code.split('_')[0], // Extract category from code
          icon: achievement.icon || "fas fa-trophy",
          points: getPointsForAchievement(achievement.code),
          isUnlocked: !!userAchievement,
          unlockedAt: userAchievement?.earnedAt,
          progress: userAchievement?.progress || 0,
          maxProgress: 100,
          requirements: getRequirementsForAchievement(achievement.code),
        };
      });

      // Calculate user stats
      const userStats = await calculateUserStats(userId);

      // Get user milestones
      const milestones = await generateUserMilestones(userId);

      res.json({
        achievements: achievementsWithProgress,
        userStats,
        milestones,
      });
    } catch (error) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  // Claim achievement reward
  app.post('/api/achievements/:achievementId/claim', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { achievementId } = req.params;

      // Check if user has this achievement
      const userAchievements = await storage.getUserAchievements(userId);
      const hasAchievement = userAchievements.some(ua => ua.achievementId === achievementId);

      if (!hasAchievement) {
        return res.status(404).json({ message: "Achievement not found or not unlocked" });
      }

      // TODO: Implement actual reward system
      // This could give rewards like:
      // - XP points to user profile
      // - Virtual currency
      // - Unlock premium features
      // - Special badges

      res.json({ 
        message: "Reward claimed successfully",
        reward: "Experience points added to your profile!"
      });
    } catch (error) {
      console.error("Error claiming achievement reward:", error);
      res.status(500).json({ message: "Failed to claim reward" });
    }
  });

  // Trigger achievement check (called after user actions)
  app.post('/api/achievements/check', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { action, data } = req.body;

      const newlyUnlocked = await checkAndUnlockAchievements(userId, action, data);

      res.json({ 
        newlyUnlocked: newlyUnlocked.length,
        achievements: newlyUnlocked,
      });
    } catch (error) {
      console.error("Error checking achievements:", error);
      res.status(500).json({ message: "Failed to check achievements" });
    }
  });
}

function getPointsForAchievement(code: string): number {
  const pointsMap: Record<string, number> = {
    'profile_complete': 50,
    'profile_first_skill': 25,
    'profile_skill_master': 100,
    'learning_first_course': 30,
    'learning_course_completed': 75,
    'learning_streak': 150,
    'career_path_set': 40,
    'career_skill_gap_analyzed': 60,
    'career_doppelganger_found': 80,
    'social_ai_chat_started': 35,
    'social_ai_chat_veteran': 200,
  };
  return pointsMap[code] || 25;
}

function getRequirementsForAchievement(code: string): string[] {
  const requirementsMap: Record<string, string[]> = {
    'profile_complete': [
      'Add profile photo',
      'Fill in basic information',
      'Add current role',
      'Upload resume',
      'Set career goals'
    ],
    'profile_first_skill': ['Add at least one skill'],
    'profile_skill_master': ['Add 10 different skills'],
    'learning_first_course': ['Enroll in any course'],
    'learning_course_completed': ['Complete any course'],
    'learning_streak': ['7-day learning streak'],
    'career_path_set': ['Set a career path goal'],
    'career_skill_gap_analyzed': ['Complete skill gap analysis'],
    'career_doppelganger_found': ['Find a career doppelgnger'],
    'social_ai_chat_started': ['Send first message to AI'],
    'social_ai_chat_veteran': ['50 AI conversations'],
  };
  return requirementsMap[code] || ['Complete the required action'];
}

function generateAllAchievements(): Achievement[] {
  return [
    // Profile Achievements
    {
      id: "profile_complete",
      name: "Profile Pioneer",
      description: "Complete your profile with all required information",
      category: "profile",
      icon: "fas fa-user-circle",
      points: 50,
      isUnlocked: false,
      maxProgress: 5,
      requirements: [
        "Add profile photo",
        "Fill in basic information",
        "Add current role",
        "Upload resume",
        "Set career goals"
      ],
    },
    {
      id: "first_skill",
      name: "Skill Seeker",
      description: "Add your first skill to your profile",
      category: "profile",
      icon: "fas fa-star",
      points: 25,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Add at least one skill"],
    },
    {
      id: "skill_master",
      name: "Skill Master",
      description: "Add 10 or more skills to your profile",
      category: "profile",
      icon: "fas fa-medal",
      points: 100,
      isUnlocked: false,
      maxProgress: 10,
      requirements: ["Add 10 different skills"],
    },

    // Learning Achievements
    {
      id: "first_course",
      name: "Learning Beginner",
      description: "Enroll in your first course",
      category: "learning",
      icon: "fas fa-graduation-cap",
      points: 30,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Enroll in any course"],
    },
    {
      id: "course_completed",
      name: "Course Finisher",
      description: "Complete your first course",
      category: "learning",
      icon: "fas fa-certificate",
      points: 75,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Complete any course"],
    },
    {
      id: "learning_streak",
      name: "Learning Streak",
      description: "Complete courses 7 days in a row",
      category: "learning",
      icon: "fas fa-fire",
      points: 150,
      isUnlocked: false,
      maxProgress: 7,
      requirements: ["7-day learning streak"],
    },

    // Career Achievements
    {
      id: "career_path_set",
      name: "Path Finder",
      description: "Set your first career path goal",
      category: "career",
      icon: "fas fa-route",
      points: 40,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Set a career path goal"],
    },
    {
      id: "skill_gap_analyzed",
      name: "Gap Analyzer",
      description: "Complete your first skill gap analysis",
      category: "career",
      icon: "fas fa-chart-bar",
      points: 60,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Complete skill gap analysis"],
    },
    {
      id: "doppelganger_found",
      name: "Mirror Mirror",
      description: "Find your first career doppelgnger",
      category: "career",
      icon: "fas fa-search",
      points: 80,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Find a career doppelgnger"],
    },

    // Social Achievements
    {
      id: "ai_chat_started",
      name: "AI Conversationalist",
      description: "Start your first AI conversation",
      category: "social",
      icon: "fas fa-robot",
      points: 35,
      isUnlocked: false,
      maxProgress: 1,
      requirements: ["Send first message to AI"],
    },
    {
      id: "ai_chat_veteran",
      name: "AI Veteran",
      description: "Have 50 conversations with AI",
      category: "social",
      icon: "fas fa-comments",
      points: 200,
      isUnlocked: false,
      maxProgress: 50,
      requirements: ["50 AI conversations"],
    },
  ];
}

async function calculateUserStats(userId: string): Promise<UserStats> {
  const [userAchievements, profile, skills] = await Promise.all([
    storage.getUserAchievements(userId),
    storage.getUserProfile(userId),
    storage.getUserSkills(userId),
  ]);

  const totalPoints = userAchievements.reduce((sum, achievement) => {
    const achievementData = generateAllAchievements().find(a => a.id === achievement.achievementId);
    return sum + (achievementData?.points || 0);
  }, 0);

  const level = Math.floor(totalPoints / 100) + 1;
  const pointsToNextLevel = (level * 100) - totalPoints;

  const allAchievements = generateAllAchievements();
  const profileCompletionPercent = calculateProfileCompletion(profile, skills);

  return {
    totalPoints,
    level,
    pointsToNextLevel,
    totalAchievements: allAchievements.length,
    unlockedAchievements: userAchievements.length,
    streakDays: 0, // TODO: Implement streak tracking
    profileCompletionPercent,
  };
}

function calculateProfileCompletion(profile: any, skills: any[]): number {
  let completion = 0;
  if (profile?.firstName) completion += 20;
  if (profile?.currentRole) completion += 20;
  if (profile?.resumeText) completion += 20;
  if (skills.length > 0) completion += 20;
  if (profile?.careerGoals) completion += 20;
  return completion;
}

function calculateAchievementProgress(achievementId: string, userId: string): number {
  // This is a simplified version - in a real implementation,
  // you'd calculate actual progress based on user data

  switch (achievementId) {
    case "profile_complete":
      return 3; // Example: 3 out of 5 profile sections completed
    case "skill_master":
      return 5; // Example: 5 out of 10 skills added
    case "learning_streak":
      return 2; // Example: 2 out of 7 days
    case "ai_chat_veteran":
      return 12; // Example: 12 out of 50 conversations
    default:
      return 0;
  }
}

async function generateUserMilestones(userId: string): Promise<Milestone[]> {
  const [profile, skills, careerPaths] = await Promise.all([
    storage.getUserProfile(userId),
    storage.getUserSkills(userId),
    storage.getUserCareerPaths(userId),
  ]);

  const milestones: Milestone[] = [];

  // Profile completion milestone
  const profileCompletion = calculateProfileCompletion(profile, skills);
  if (profileCompletion < 100) {
    milestones.push({
      id: "complete_profile",
      title: "Complete Your Profile",
      description: "Fill out all sections of your profile to unlock personalized recommendations",
      category: "profile",
      targetValue: 100,
      currentValue: profileCompletion,
      isCompleted: false,
      reward: "50 XP + Personalized recommendations",
    });
  }

  // Skills milestone
  if (skills.length < 5) {
    milestones.push({
      id: "add_skills",
      title: "Build Your Skill Portfolio",
      description: "Add at least 5 skills to get better career matching",
      category: "skills",
      targetValue: 5,
      currentValue: skills.length,
      isCompleted: false,
      reward: "75 XP + Advanced matching",
    });
  }

  // Career path milestone
  if (careerPaths.length === 0) {
    milestones.push({
      id: "set_career_path",
      title: "Define Your Career Journey",
      description: "Set your first career path to start tracking progress",
      category: "career",
      targetValue: 1,
      currentValue: 0,
      isCompleted: false,
      reward: "100 XP + Path tracking",
    });
  }

  return milestones;
}

async function checkAndUnlockAchievements(userId: string, action: string, data: any): Promise<Achievement[]> {
  // This would check if any achievements should be unlocked based on the user's action
  // For example: if action === "profile_updated", check profile completion achievements
  // Return any newly unlocked achievements

  return [];
}

